mapper.select:
select strGoodsId, strGoodsBarCode, strUnitId, strUnitName, strGoodsName, strGoodsTypeId, strGoodsTypeName, strSupplierName, iRequiredIntegral, dEnterPrice, dSalePrice, iStock, iPreferentialType, iState, txtGoodsDesc, txtGoodsDescDetail, strEmployeeId, strEmployeeName, strEmployeeLoginName, strInsertTime, strUpdateTime from tb_goods
iPreferentialType=#{iPreferentialType} and
strGoodsTypeId=#{strGoodsTypeId} and iState=1 limit #{iPageFrom},#{iPageSize}

select strServiceInfoId, strServiceInfoName, strServiceTypeId, strServiceTypeName, dSalePrice, strServiceBarCode, strUnitId, strUnitName, strSupplierName, iPreferentialType, iState, txtServiceDesc, txtServiceDescDetail, strEmployeeId, strEmployeeName, strEmployeeLoginName, strInsertTime, strUpdateTime, strReserved from tb_service_info
iPreferentialType=#{iPreferentialType} and
strServiceTypeId=#{strServiceTypeId} and iState=1 limit #{iPageFrom},#{iPageSize}

mapper.update:
update tb_orderDetail_info set iStatus=#{iStatus}, iPayStandard=#{iPayStandard}, strPayTime=#{strPayTime}, iPayType=#{iPayType},
strThirdPartyTradeFlow=#{strThirdPartyTradeFlow},
strLastAccessedTime=#{strLastAccessedTime} where strOrderNum=#{strOrderId}

update tb_purchase_order set iStatus=#{iStatus}, iPayStandard=#{iPayStandard}, strPayTime=#{strPayTime}, iPayType=#{iPayType},
strThirdPartyTradeFlow=#{strThirdPartyTradeFlow},
strLastAccessedTime=#{strLastAccessedTime} where strOrderId=#{strOrderId}

 update tb_orderDetail_info set 
	iIntegrationAmount=#{iIntegrationAmount},
	iPurchaseIntegrationAmount=#{iPurchaseIntegrationAmount}
	where strOrderId=#{strOrderId}

update tb_member
set DBALANCE=#{bgRestAmount}
where STRMEMBERID=#{strMemberId} and INTSTATUS=1
