mapper.select:
select count(strActivityId) from tb_consumePresents_Activity

	
		and strActivityEndTime <#{strCurrentDate}
	
	
	
		and strActivityEndTime >=#{strCurrentDate}
	
	
	
		and strLevelsId=#{strSearchMemberLevelId}

select strActivityId, strActivityName, strLevelsId, strActivityBeginTime, strActivityEndTime, strActivityKinds, strIsCumulation, strEmployeeId, strEmployeeName, strEmployeeRealName, strCreationTime, strLastAccessedTime from tb_consumePresents_Activity
and strActivityEndTime <#{strCurrentDate}
and strActivityEndTime >=#{strCurrentDate}
and strLevelsId=#{strSearchMemberLevelId}
order by strCreationTime DESC limit #{iPageFrom},#{iPageSize}
