mapper.update:
update tb_userDefinedPresents_Integration set strActivityId=#{strActivityId}, iEnabled=#{iEnabled}, strEmployeeId=#{strEmployeeId}, iPresentsIntegration=#{iPresentsIntegration}, strEmployeeName=#{strEmployeeName}, strEmployeeRealName=#{strEmployeeRealName},
strCreationTime=#{strCreationTime},
strLastAccessedTime=#{strLastAccessedTime} where strPresentsIntegrationId=#{strPresentsIntegrationId}

update tb_userDefinedPresents_storedValue set strActivityId=#{strActivityId}, dPresentsAmount=#{dPresentsAmount}, iEnabled=#{iEnabled}, strEmployeeId=#{strEmployeeId}, strEmployeeName=#{strEmployeeName}, strEmployeeRealName=#{strEmployeeRealName},
strCreationTime=#{strCreationTime},
strLastAccessedTime=#{strLastAccessedTime} where strPresentsStoredValueId=#{strPresentsStoredValueId}

mapper.select:
select count(strActivityId) from tb_userDefinedPresents_Activity

	
		and strActivityEndTime <#{strCurrentDate}
	
	
	
		and strActivityEndTime >=#{strCurrentDate}
	
	
	
		and strLevelsId=#{strSearchMemberLevelId}

select strActivityId, strActivityName, strLevelsId, strActivityBeginTime, strActivityEndTime, iActivityKinds, strEmployeeId, strEmployeeName, strEmployeeRealName, strCreationTime, strLastAccessedTime from tb_userDefinedPresents_Activity
and strActivityEndTime <#{strCurrentDate}
and strActivityEndTime >=#{strCurrentDate}
and strLevelsId=#{strSearchMemberLevelId}
order by strCreationTime DESC limit #{iPageFrom},#{iPageSize}
